<?php
class TheParent
{
    const SETUP = ['TEST' => 'HOI'];

    public function whatAmI()
    {
        return static::class;
    }
}

class TheChild extends TheParent
{
    public function testSomething(string $variable): string
    {
        return (mb_strlen($variable)) ? mb_substr($variable, 0, 1) : 'X';
    }
}

class TheGrandChild extends TheChild
{
    public function testSomething(string $variable): string
    {
        return mb_strtoupper($variable);
        // return parent::testSomething($variable); // TODO: Change the autogenerated stub
    }

    public function whatAmI()
    {
        return 'zoo';
        //return parent::whatAmI(); // TODO: Change the autogenerated stub
    }
}

abstract class TheAbstract
{
