    {
    }
}


class World
{
    public function hello(): string
    {
        return "Hello " . __CLASS__;
    }
}

class Meek extends World
{
    public function __call(string $name, array $arguments)
    {
        return "I have the world";
    }
}

class A
{
    public static function foo() {
        echo static::who();
    }

    public static function who() {
        return 'A';
    }
}

class B extends A
{
    public static function test() {
        A::foo();
        parent::foo();
        self::foo();
    }
}

class C extends B
{
    public static function who() {
        echo 'C';
    }
}

// GENERATOR
function sowCrops() { return 'wheat'; }
function millWheat() { sleep(1); return 'flour'; }
function bake($flour) { return 'cupcake'; }

function generator() {
    $wheat = yield sowCrops();
    $flour = yield millWheat();
    return bake($flour);
}
$gen = generator();
foreach ($gen as $key => $value) {
    echo $key . ' => ' . $value . PHP_EOL;
}
var_dump(basename('index.php'));
// echo $gen->getReturn();
exit;
C::test();
exit;

